// Inquiry processing logic
const handleInquiry = async (data) => {
  const res = await fetch('/api/inquiry', { method: 'POST', body: JSON.stringify(data) });
  return res.json();
}

// Smooth scroll to section
const scrollTo = (id) => {
  document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
};

// Reveal animations
const observer = new IntersectionObserver((entries) => {
  entries.forEach(entry => {
    if (entry.isIntersecting) entry.target.classList.add('reveal-active');
  });
});

// Real-time tab synchronization via BroadcastChannel
useEffect(() => {
  const bc = new BroadcastChannel('task-channel');
  bc.onmessage = (ev) => {
    if (ev.data?.type === 'update_projects') {
      setProjects(ev.data.payload);
    }
  };
  return () => bc.close();
}, []);

// Action to re-trigger failed notifications
@action(detail=True, methods=['post'])
def retry(self, request, pk=None):
    notification = self.get_object()
    if notification.status == Notification.STATUS_FAILED:
        notification.status = Notification.STATUS_PENDING
        notification.save()
        send_notification_task.delay(notification.id)
        return Response({'status': 'retry triggered'})
